mapper.select:
select * from cc_area_type
 WHERE dr=0

     and id in

         #{tempId}



     AND area_type_name LIKE concat('%',#{areaTypeName},'%')


     AND area_type_code =#{areaTypeCode}


     and created_time >= #{startTime}


     and created_time <= #{endTime}


     and updated_time >= #{updateStartTime}


     and updated_time <= #{updateEndTime}

 order by updated_time desc

SELECT
      *
  FROM
      cc_area_type a
  WHERE
      a.dr = 0
  AND a.parent_id=#{id}
  AND a.tenant_id = #{tenantId}
  AND a.app_id = #{appId}

select a.* from cc_area_type a where a.dr=0
  AND a.tenant_id = #{tenantId}
  AND a.app_id = #{appId}

      AND a.path like concat('%',#{idPath}, '%')

select * from cc_area_type where 1=1


    and id in

        #{tempId}
